const cb = require("./index.js");

async function go() {
    const context = [];
    let line = "Hi.";
    
    while (true) {
        try {
            const response = await cb(line, context);
            context.push(line);
            context.push(response);
            console.log(response);
            line = response;
        } catch (err) {
            console.log("Failed");
        }
    }
}
go();